mapper.insert:
insert
	into
		rodo_sendmessage
		(
			sendmessage_num
			, sendmessage_sender
			, sendmessage_receiver
			, sendmessage_title
			, sendmessage_text
		)
		
values
		(
			rodo_sendmessage_seq.nextval
			, #{sender}
			, #{receiver}
			, #{title}
			, #{text}
		)

insert
	into
		rodo_receivemessage
		(
			receivemessage_num
			, receivemessage_sender
			, receivemessage_receiver
			, receivemessage_title
			, receivemessage_text
		)
		
values
		(
			rodo_receivemessage_seq.nextval
			, #{sender}
			, #{receiver}
			, #{title}
			, #{text}
		)

mapper.select:
select sendmessage_num messagenum , sendmessage_sender sender , sendmessage_receiver receiver , sendmessage_title title , sendmessage_text text , to_char(sendmessage_send_dt, 'yyyy.MM.dd HH:MI') senddate , sendmessage_read_fl flag
from rodo_sendmessage
where sendmessage_num = #{sendmessagenum}

select receivemessage_num messagenum , receivemessage_sender sender , receivemessage_receiver receiver , receivemessage_title title , receivemessage_text text , to_char(receivemessage_send_dt, 'yyyy.MM.dd HH:MI') senddate , receivemessage_read_fl flag
from rodo_receivemessage
where receivemessage_num = #{receivemessagenum}

select count(*)
from rodo_receivemessage
where receivemessage_receiver = #{id}

select count(*)
from rodo_sendmessage
where sendmessage_sender = #{id}

mapper.delete:
delete from rodo_receivemessage
where receivemessage_num = #{messagenum}
and receivemessage_receiver = #{receiver}

delete from rodo_sendmessage
where sendmessage_num = #{messagenum}
and sendmessage_sender = #{sender}
