mapper.insert:
insert into
	rodo_freeboard(
		free_boardnum
		, free_id
		, free_title
		, free_content
	)
	values(
		rodo_freeboard_seq.nextval
		, #{free_id}
		, #{free_title}
		, #{free_content}
	)

insert into
	rodo_freefile(
		free_boardnum
		
			, freefile_original
			, freefile_saved
		
		)
	values(
		#{free_boardnum}
		
			, #{freefile_original}
			, #{freefile_saved}
		
	)

insert
	into
		rodo_freereply
		(
			freereply_replynum
			, free_boardnum
			, freereply_id
			, freereply_text
		)
		
	values
		(
			rodo_freereply_seq.nextval
			, #{free_boardnum}
			, #{freereply_id}
			, #{freereply_text}
		)

mapper.select:
select freereply_replynum , free_boardnum , freereply_id , freereply_text , freereply_input_dt , freereply_update_dt
from rodo_freereply
where freereply_replynum = #{freereply_replynum}

mapper.delete:
delete from rodo_freereply
where freereply_replynum = #{freereply_replynum}
and free_boardnum = #{free_boardnum}
