mapper.insert:

	insert 
		into
			rodo_gpsboard
			(
				gps_boardnum
				, gps_id
				, gps_title
				, gps_content
				, gps_gpxinfo
				, gps_location
				, gps_marker
				, gpsfile_original
				, gpsfile_saved
			)
			
			values
			(
				rodo_gpsboard_seq.nextval
				, #{gps_id}
				, #{gps_title}
				, #{gps_content}
				, #{gps_gpxinfo}
				, #{gps_location}
				, #{gps_marker}
				, #{gpsfile_original}
				, #{gpsfile_saved}
			)

mapper.update:
	update 
		rodo_gpsboard
	set
		gps_title = #{gps_title}
		, gps_content = #{gps_content}
		, gps_update_dt = sysdate
	
		, gpsfile_original = #{gpsfile_original}
		, gpsfile_saved = #{gpsfile_saved}
	
	where
		gps_boardnum = #{gps_boardnum}
	and
		gps_id = #{gps_id}
