mapper.insert:
insert
	into
		rodo_videoboard
		(
			video_boardnum
			, video_id
			, video_title
			, video_content
			, video_originalfile
			, video_savedfile
			, video_thumbnail
		)
		
		values
		(
			rodo_video_seq.nextval
			, #{video_id}
			, #{video_title}
			, #{video_content}
			, #{video_originalfile}
			, #{video_savedfile}
			, #{video_thumbnail}
		)

mapper.select:
select free_boardnum , free_id , free_title , free_content , free_input_dt , free_update_dt , free_hit
from rodo_freeboard
where free_boardnum = #{free_boardnum}

mapper.delete:
delete from rodo_freeboard
where free_boardnum = #{free_boardnum}
and free_id = #{free_id}

mapper.update:
update
	rodo_freeboard
	
set 
	free_title = #{free_title}
	, free_content = #{free_content}
	
	
	
		, freefile_original = #{freefile_original}
		, freefile_saved = #{freefile_saved}
		
	

where 
	free_boardnum = #{free_boardnum}

mapper.insert:
insert
	into
		rodo_freereply
		(
			freereply_replynum
			, free_boardnum
			, freereply_id
			, freereply_text
		)
		
	values
		(
			rodo_freereply_seq.nextval
			, #{free_boardnum}
			, #{freereply_id}
			, #{freereply_text}
		)

mapper.select:
select freereply_replynum , free_boardnum , freereply_id , freereply_text , freereply_input_dt , freereply_update_dt
from rodo_freereply
where freereply_replynum = #{freereply_replynum}

mapper.delete:
delete from rodo_freereply
where freereply_replynum = #{freereply_replynum}
and free_boardnum = #{free_boardnum}
