mapper.insert:
insert
	into
		rodo_videoboard
		(
			video_boardnum
			, video_id
			, video_title
			, video_content
			, video_originalfile
			, video_savedfile
			, video_thumbnail
		)
		
		values
		(
			rodo_video_seq.nextval
			, #{video_id}
			, #{video_title}
			, #{video_content}
			, #{video_originalfile}
			, #{video_savedfile}
			, #{video_thumbnail}
		)


	insert
		into
			rodo_videoreply
			(
				videoreply_replynum
				, video_boardnum
				, videoreply_id
				, videoreply_text
			)
			
			values
			(
				rodo_videoreply_seq.nextval
				, #{video_boardnum}
				, #{videoreply_id}
				, #{videoreply_text}
			)

mapper.delete:
delete from rodo_videoreply
where videoreply_replynum = #{videoreply_replynum}
and video_boardnum = #{video_boardnum}
