	into
		rodo_videoboard
		(
			video_boardnum
			, video_id
			, video_title
			, video_content
			, video_originalfile
			, video_savedfile
			, video_thumbnail
		)
		
		values
		(
			rodo_video_seq.nextval
			, #{video_id}
			, #{video_title}
			, #{video_content}
			, #{video_originalfile}
			, #{video_savedfile}
			, #{video_thumbnail}
		)
